script:
employees = frappe.get_all("Employee", filters={"status": "Active"}, fields=["name", "employment_type","custom_standard_working_hours"])
standard_working_days = frappe.db.get_single_value('HR Settings', 'custom_standard_working_days_in_month')

for emp in employees:
    latest_assignment = frappe.get_all(
        "Salary Structure Assignment",
        filters={"employee": emp.name, "docstatus": 1},  # Only submitted assignments
        fields=["base", "salary_structure", "from_date"],
        order_by="from_date desc",
        limit=1
    )
    if latest_assignment:
        base_salary = latest_assignment[0].get("base")
        if emp.employment_type == "Worker":
            frappe.db.set_value("Employee", emp.name, "custom_gross_salary", base_salary)
        else:
            frappe.db.set_value("Employee", emp.name, "ctc", base_salary)

        try:
            if emp.custom_standard_working_hours and float(emp.custom_standard_working_hours) > 0 and standard_working_days > 0:
                per_day = base_salary / standard_working_days
                per_hour = per_day / float(emp.custom_standard_working_hours)

                frappe.db.set_value("Employee", emp.name, "custom_per_day_wages", per_day)
                frappe.db.set_value("Employee", emp.name, "custom_per_hour_wages", per_hour)

        except ZeroDivisionError:
            frappe.log_error(f"ZeroDivisionError: Skipping calculation for Employee {emp.name} due to zero working hours or working days.")

applicable_ot = doc.custom_applicable_overtime
per_hour_wage = frappe.db.get_value('Employee', doc.employee, 'custom_per_hour_wages')

if applicable_ot and per_hour_wage:
    overtime_payment = round(applicable_ot * per_hour_wage)
    component_found = False
    esi_found = False
    before_deduction = doc.total_deduction

    for row in doc.deductions:
        if row.salary_component == "ESI":
            before_deduction = doc.total_deduction - row.amount
            esi_found = True
            break

    for row in doc.earnings:
        if row.salary_component == "Overtime Payment":
            row.amount = overtime_payment
            component_found = True
            break

    if not component_found:
        doc.append("earnings", {
            "salary_component": "Overtime Payment",
            "amount": overtime_payment
        })

    if overtime_payment:
        new_gross = doc.gross_pay + overtime_payment
        esi_amount = new_gross * 0.0075 if esi_found else 0

        if esi_found:
            for row in doc.deductions:
                if row.salary_component == "ESI":
                    row.amount = esi_amount
                    break

        new_deduction = before_deduction + esi_amount
        new_net_pay = new_gross - new_deduction

        doc.gross_pay = new_gross
        doc.total_deduction = new_deduction
        doc.net_pay = new_net_pay
        doc.rounded_total = new_net_pay
